package com.day5.branching;

public class BreakStatements {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
				for (int i = 1; i <= 10; i++) {
					if (i == 5)
						break;
					System.out.println(i + " ");
					
					if (i == 5) continue;
						
				}

			}

		}

	


